INSERT INTO `doctors` (`id`, `name`, `photo`, `education`, `work_experience`, `text`, `specialization`, `children`) VALUES
(1, 'Анощенкова Лариса Викторовна', './img/doctors/afonchenkova.jpg', 'СГМИ, интернатура, ординатура', 'с 1992 года', '', 'акушерство и гинекология: поликлиническое, неоперативное, оперативное,\r\nгинекология детского возраста, \r\nэндокринология - гинекология, \r\nпланирование и ведение беременности\r\nневынашивание беременности,\r\nгинекология климактерического периода,\r\nкольпоскопия,\r\nпатология шейки матки.', 0),
(2, 'Буцык Ирина Игоревна', './img/doctors/butsik.jpg', '2002 ординатура при СГМА', '', '', 'Главный врач ООО «МЦ «УЗИ+»\r\nврач акушер-гинеколог высшей категории, врач ультразвуковой диагностики высшей категории.\r\nКандидат медицинских наук.\r\nОбладатель международного сертификата Фонда Медицины Плода (FMF-сертификат).', 1),
(3, 'Никифоровская Елена Николаевна', './img/doctors/nikiforovskaya.jpg', 'Кандидат медицинских наук.\r\nС 2014– доцент кафедры акушерства и гинекологии с курсом пренатальной диагностики\r\nокончила СГМА в  1999 г по специальности лечебное дело (диплом с отличием).\r\n2001 – окончила ординатуру по специальности акушерство и гинекология СГМА (курсы по повышению квалификации по данной специальности проходила в 2006, 2010, 2015, 2020 годах). \r\n2001 – учеба по диагностической и хирургической эндоскопии в гинекологии. \r\n2021 – лапароскопия в гинекологии, лапароскопический шов (г Санкт – Петербург)\r\n2002 – специализация по ультразвуковой диагностике СГМА (курсы по повышению квалификации по УЗД проходила в 2007,  2012, 2017, 2021 годах)\r\n2013 – пренатальная диагностика нарушений развития ребенка  (РМАНПО г Москва); \r\n2018 – пренатальная (дородовая) диагностика нарушений развития ребенка 2 триместр (РМАНПО г Москва)\r\n2018 – детальный осмотр сердца плода («Медика» ,г Санкт – Петербург), \r\n2021 – обучение в РМАНПО на курсе тематического усовершенствования «Ультразвуковая диагностика поверхностно расположенных органов)\r\nЗащитила кандидатскую дисертацию, \r\n2003 – присуждена ученая степень кандидата медицинских наук.', '21 год', '\r\n', 'диагностика и лечение гинекологических заболеваний, в том числе патологии эндометрия (полипов эндометрия, гиперплазий эндометрия), миомы матки, эндометриоза; владение гистероскопией, лапароскопическими и лапаротомными гинекологическими операциями, \r\nУЗИ органов малого таза, молочной железы, исследования беременных на всех сроках, скрининговые исследования в 1, 2 и 3 триместрах беременности, сертификат FMF; член пренатального консилиума.', 0),
(4, 'Волкова Людмила Викторовна', './img/doctors/volkova.jpg', '', '', '', '', 0),
(5, 'Федянова Марина Петровна ', './img/doctors/fedianiva.jpg', '', '', '', '', 0),
(6, 'Щербакова Алеся Валерьевна', './img/doctors/sherbakova.jpg', '2008 год окончание СГМА по специальности лечебное дело. \r\n2008-2010 ординатуры по акушерству и гинекологии. \r\n2010-2013 года аспирантура. \r\nС 2013 года врач гинеколог и УЗДГ ООО «МЦ «УЗИ+». \r\n2017 год повышение квалификации «Гинекологическая эндокринология», ФГБОУ ВО МГМСУ им. А.И.Евдокимова Минздрава России\r\nАкушерство и гинекология: Автономная некоммерческая организация дополнительного профессионального образования «Гуманитарно-технический институт» г. Москва,\r\n2019 год повышение квалификации «Патология шейки матки и кольпоскопия», Федеральное государственное автономное образовательное учреждение высшего образования «Первый Московский государственный медицинский университет имени И.М. Сеченова» \r\n2020 год повышение квалификации по акушерству и гинекологии , \r\n2021 год повышение квалификации по УЗДГ, ФГБОУ ВС СГМУ МЗ РФ г. Смоленск.\r\n', '12 лет', '', 'Врач акушер-гинеколог и УЗДГ. \r\nдиагностика и лечение гинекологических заболеваний,\r\nэндокринная гинекология. ', 0),
(7, 'Морозова Оксана Владимировна', './img/doctors/morozova.jpg', 'СГМИ лечебное дело-1994 год, \r\nинтернатура по аллергологии-иммунологии 1995 год СГМА\r\nСертификат по аллергологии-иммунологии 2017 год\r\nПервичная переподготовка по терапии 2018 год, сертификат по терапии 2018 год\r\nПрофессиональная переподготовка по аллергологи-иммунологии: 1998, 2003, 2008, 2012, 2017\r\nКурсы по аэропалинации МГУ биологический факультет- 2003, 2004\r\nШкола по АСИТ 2022 год', '28 лет', '', 'Врач аллерголог-иммунолог, терапевт\r\nВысшая квалификационная категория,\r\nДиагностика и лечение аллергических заболеваний, \r\nпроведение аллергообследования и тестирования, \r\nпроведение лечения аллергических заболеваний инновационными препаратами и методами, \r\nдиагностика иммунокомпрометированных заболеваний и иммунодефицитных состояний.\r\n', 1),
(8, 'Сергеева Ирина Николаевна', './img/doctors/sergeeva.jpg', 'Член РААКИ, член ассоциации детских аллергологов-иммунологов,\r\nчлен областного общества дерматовенерологов.\r\nНаграждена грамотой областной думы (2022 год), победитель конкурса «Лучший врач» (2018 год).\r\nВрач аллерголог-иммунолог, высшая категория, \r\nСГМА 1995 год, \r\nинтернатура по клинической аллергологии и иммунологии 1995- 1996 год, \r\nклиническая ординатура аллергология- иммунология 1996- 1998 год. \r\nПовышение квалификации каждые 5 лет. \r\nЕжегодно участие в международных конференциях РААКИ. \r\nСГМА профессиональная переподготовка по дерматовенерологии 2008 год, \r\nповышение квалификации каждые пять лет. \r\n', '', '', 'Лечение и профилактика аллергических заболеваний кожи, глаз, носоглотки, трахеи, бронхов, желудочно-кишечного тракта. \r\nИммунопрофилактика инфекций. \r\nЛечение часто болеющих детей и взрослых с выявлением врожденных заболеваний иммунитета. \r\nПодготовка детей к началу посещения детского учреждения в виде адаптационных профилактических курсов терапии.\r\nСоставление элиминационных диет для детей с пищевой аллергией. \r\nИммунитет и аллергия у беременных, профилактика и лечение. \r\nЛечение хронической герпетической инфекции. \r\nВрач дерматовенеролог,  первая категория. \r\nсправки в бассейн детское учреждение, \r\nлечение инфекций кожи, болезней волос и ногтей. \r\nдерматоскопия невусов.', 0),
(10, 'Будрин Владимир Анатольевич', './img/doctors/budrin.jpg', 'Смоленская государственная медицинская академия (СГМА), лечебный факультет 1995 г.\r\nСГМА, интернатура и ординатура по хирургии 1995-1997 г.г.\r\nСГМА - первичная специализация по эндоскопии 1997 г.,\r\nФГБОУ ВО РНИМУ им. Н.И. Пирогова (Москва) - цикл обучения \"ЭРПХГ и эндобилиарные операции\" 2001 г.\r\nзащита диссертации \"Методы озонотерапии в лечении больных с ушитыми перфоративными гастродуоденальными язвами\", присвоено звание кандидата медицинских наук 2006 г.\r\nСеверо-западный государственный медицинский университет им. И.И. Мечникова (Санкт-Петербург) - цикл усовершенствования по специальности \"Эндоскопия\" 2012 г.\r\nСмоленский государственный медицинский университет - повышение квалификации по эндоскопии 2017 и 2022 г.г.\r\nАккредитация - июнь 2022 г.\r\nС 2007 г. - высшая квалификационная категория.\r\nКандидат медицинских наук.\r\nВысшая категория.', 'с 1997 года', '', 'Врач-эндоскопист.\r\nгастродуоденоскопия,\r\nколоноскопия,\r\nректороманоскопия,\r\nбронхоскопия,\r\nЭРХПГ с папиллосфинктеротомией удалением конкрементов из желчных потолков;\r\nполипэктомия из верхних и нижних отделов ЖКТ; \r\nметодики эндоскопического гемостаза (аппликация, инфильтрация, диатермо- и аргоноплазменная коагуляция,  склеротерапия), \r\nэндоскопическое бужирование,\r\nудаление лигатур, инородных тел, \r\nэндобронхивльная санация, лаваж трахео-бронхиального дерева. \r\n', 0),
(11, 'Лукьянова Анжела Васильевна', './img/doctors/lukianova.jpg', 'СГМИ (лечебное дело) .1988-89г.\r\nИнтернатура по терапии1989-1995г. работа врачом терапевтом ЦРБ Куйбышевского района Калужской области. \r\nСтажировка по эндоскопии в 1993. \r\nС 1995года по настоящее время работа врачом эндоскопического отделения ОБУЗ КБ N1.\r\nПовышение квалификации по эндоскопии ФУВ СГМИ 1995-96г., 1999,2004,2009,2014,2019\r\nВысшая квалификационная категория по эндоскопии.', 'с 1995 года', '', 'Врач-эндоскопист\r\n ЭГДС с цитоморфологией.\r\nбронхоскопия,\r\nректороманоскопия, \r\nколоноскопия, \r\nЭРПХГ с ПСТ, \r\nназобиллиарное и внутреннее дренированние желчных и панкреатических протоков, \r\nполипэктомия из верхних и нижних отделов ЖКТ, \r\nметодики эндоскопического гемостаза (аппликация, инфильтрация, диатермокоагуляция, склеротерапия), \r\nэндоскопическое бужирование, \r\nудаление лигатур, инородных тел, \r\nпостановка назогастрального, назодуоденального,толстокишечного зондов, \r\nэндобронхивльные санации, \r\nлаваж трахео-бронхиального дерева.', 0),
(12, 'Прохоренко Татьяна Игоревна', './img/doctors/prohorenko.jpg', '2002 г. Смоленская государственная медицинская академия, лечебный факультет. \r\n2002-2005 г. г. СГМА, интернатура, ординатура по хирургии. \r\n2013 г., СГМА - первичная специализация по онкологии, \r\n2017 г, СГМА - профессиональная переподготовка по специальности эндоскопия. Аккредитация по эндоскопии \r\n2022 г. Кандидат медицинских наук\r\n2008 г. Институт хирургии имени А. В. Вишневского. Высшая категория с 2019\r\n', '', '', 'хирургия, онкология, эндоскопия. Высшая категория. Кандидат медицинских наук.', 0),
(13, 'Ряжечкин Дмитрий Александрович', './img/doctors/riajechkin.jpg', '', '', '', '', 0),
(14, 'Асафьева Олеся Юрьевна', './img/doctors/actafieva.jpg', 'В 2003 г. закончила СГМА по специальности лечебное дело. \r\n2004г терапия, СГМА. Категория высшая. \r\n2006г пульмонология, СГМА,\r\n2020г гастроэнтерология, СГМА,\r\n2020г трансфузиология, СГМА,\r\n2021г кардиология,  Москва, ООО «ПрофАудитКонсалт»,\r\nрегулярное повышение квалификации в СГМУ, сертификаты по организации здравоохранения и экспертизе временной нетрудоспособности.\r\n', '', '', 'Заведующая отделением неотложной терапии ОГБУЗ КБСМП с 2008 г.\r\nлечение и дифференциальная диагностика всех терапевтических, кардиологических, гастроэнтерологических, пульмонологических заболеваний, \r\nинтерпретация результатов лабораторных, эндоскопических, ультразвуковых методов обследования\r\nснятие ЭКГ, \r\nвыполнение ФВД, \r\nвладение методикой проведения плевральных пункций, \r\nзаместительная терапия компонентами крови на базе стационара\r\n ', 0),
(15, 'Жлобницкая Лилия Александровна', './img/doctors/jlobnitskaya.jpg', 'СГМА 2000 год \"Педиатрия\".\r\nОрдинатура по специальности \"Педиатрия\" 2000-2003 гг. \r\nПрофессиональная переподготовка по специальности \"Терапия\" 2003 г. \r\nПрофессиональная переподготовка по специальности \"Гастроэнтерология\" 2006 г. \r\nПовышение квалификации по специальности \"Гастроэнрология \"2011, 2016 гг.- СГМУ. 2021 г.\"Сибирский институт непрерывного медицинского образования \" ЧУ ДПО \"Институт переподготовки и повышения квалификации специалистов здравоохранения \" (г.Новосибирск). \r\nАккредитация-2021 год.\r\n', '20 лет', '', 'оказание консультативной, диагностической и лечебной помощи детскому и взрослому населению с гастроэнтерологическими заболеваниями;\r\nдиспансерное наблюдение и медицинская реабилитация больных с гастроэнтерологическими заболеваниями;\r\nпроведение мероприятий по первичной профилактике развития гастроэнтерологических заболеваний, а также вторичной профилактике осложнений и прогрессирующего течения гастроэнтерологических заболеваний.', 0),
(16, 'Коваленко Марина Александровна', './img/doctors/kovalenko.jpg', 'СГМИ - педиатрия. 1990г\r\nСГМИ- интернатуру по педиатрии 1991. \r\nСГМА проф.переподгатовкв по гастроэнтерологии 2011. \r\nСГМУ-повышение квалификации по гастроэнтерологии 2016. \r\nСГМУ-актуальные вопросы детской гастроэнтерологии 2019.\r\nСГМУ-повышение квалификации по гастроэнтерологии 2021', '', '', 'лечение и диагностика заболеваний ЖКТ', 0),
(17, 'Хуторова Елена Александровна', './img/doctors/hutorova.jpg', '1998 СГМА по специальности «Лечебное дело»\r\n1998 интернатура, Кафедра госпитальной терапии СГМА, МЛПУ КБСМП, г. Смоленск\r\n2000 ФПК и ППС СГМА профессиональная переподготовка «Функциональная диагностика»\r\n2001 Кафедра клинической фармакологии СГМА тематическое усовершенствование «Качественная клиническая практика для исследователей»\r\n2003 ФПУ и ППС СГМА Профессиональная переподготовка Цикл «Пульмонология»\r\n2004 ГОУ ВПО ММА имени     И. М.Сеченова Минздрава России Тематическое усовершенствование «Психосоматические расстройства в общемедицинской практике»\r\n2005 ФПК и ППС СГМА Тематическое усовершенствование Цикл «Терапия»\r\n2009 ФПК и ППС СГМА Тематическое усовершенствование Цикл «Функциональная диагностика»\r\n2010 ФПК и ППС СГМА Тематическое усовершенствование Цикл «Терапия»\r\n2011 ФПК и ППС СГМА Тематическое усовершенствование Цикл «Пульмонология»\r\n2012 ФПК и ППС СГМА Профессиональная переподготовка Цикл «Гастроэнтерология»\r\n2015 ФПК и ППС СГМА Тематическое усовершенствование Цикл «Функциональная диагностика»\r\n2015 ФПК и ППС СГМА Тематическое усовершенствование Цикл «Терапия»\r\n2016 Ф ДПО  СГМУ Тематическое усовершенствование Цикл «Пульмонология»\r\n2017 ФПК и ППС СГМА Тематическое усовершенствование Цикл «Гастроэнтерология»\r\n2020 Региональный центр профессионального образования «Образовальный стандарт» Тематическое усовершенствование Цикл «Функциональная диагностика»\r\n2020 Региональный центр профессионального образования «Образовальный стандарт» Тематическое усовершенствование Цикл «Терапия»\r\n2020 ФПК и ППС СГМА Тематическое усовершенствование Цикл «Пульмонология»', '26 лет', '', 'Врач терапевт, гастроэнтеролог, пульмонолог\r\nВрач функциональной диагностики.\r\nдиагностика и лечение: \r\nбронхиальной астмы, \r\nХОБЛ, \r\nпневмоний, \r\nишемической болезни сердца, \r\nартериальной гипертензии, \r\nаритмий, \r\nязвенной болезни, \r\nболезней желудка, \r\nжелчного пузыря, \