INSERT INTO `doctors` (`id`, `name`, `photo`, `work_experience`, `position`, `specialization`, `education`, `pediatrician`, `adult`, `gagarina_place`, `popova_place`, `text`) VALUES
(1, 'Анощенкова Лариса Викторовна', './img/doctors/anoshenkova.jpg', 'с 1992 года', 'Врач акушер-гинеколог. Высшая квалификационная категория.', 'Акушерство и гинекология: поликлиническое, неоперативное, оперативное,\r\nгинекология детского возраста, \r\nэндокринология - гинекология, \r\nпланирование и ведение беременности\r\nневынашивание беременности,\r\nгинекология климактерического периода,\r\nкольпоскопия,\r\nпатология шейки матки.', 'СГМИ, интернатура, ординатура', 1, 1, 1, 0, ''),
(2, 'Буцык Ирина Игоревна', './img/doctors/butsik.jpg', '23 года.', 'Главный врач ООО «МЦ «УЗИ+»\r\n\r\n', 'Врач акушер-гинеколог высшей категории, врач ультразвуковой диагностики высшей категории.\r\nКандидат медицинских наук.\r\nОбладатель международного сертификата Фонда Медицины Плода (FMF-сертификат).', '2000 год – СГМА по специальности «Лечебное дело»\r\n2002 ординатура при СГМА по специальности «Акушерство и гинекология»\r\n2002 год – переподготовка по специальности «Ультразвуковая диагностика» (СГМА)\r\n2004 год – присуждена ученая степень кандидата медицинских наук\r\n2012 год – переподготовка по специальности «Организация здравоохранения» (СГМА)\r\n2017 год – повышение квалификации «Акушерство и гинекология» (СГМУ) \r\n2017 год – повышение квалификации «Научные основы и технологии управления здравоохранением» (СГМУ)\r\n2021 год – повышение квалификации «Ультразвуковая диагностика» (СГМУ)\r\n2022 год - повышение квалификации «Акушерство и гинекология» (ООО Региональный центр профессионального образования  «Образовательный стандарт»)\r\n2022 год - повышение квалификации «Организация здравоохранения и общественное здоровье» (ООО Региональный центр профессионального образования  «Образовательный стандарт»)\r\n2022 год – аккредитация по специальности «Ультразвуковая диагностика»\r\n2022 год аккредитация по специальности «Организация здравоохранения»\r\n', 1, 1, 1, 1, ''),
(3, 'Никифоровская Елена Николаевна', '.', '21 год', '', 'диагностика и лечение гинекологических заболеваний, в том числе патологии эндометрия (полипов эндометрия, гиперплазий эндометрия), миомы матки, эндометриоза; владение гистероскопией, лапароскопическими и лапаротомными гинекологическими операциями, \r\nУЗИ органов малого таза, молочной железы, исследования беременных на всех сроках, скрининговые исследования в 1, 2 и 3 триместрах беременности, сертификат FMF; член пренатального консилиума.', 'Кандидат медицинских наук.\r\nС 2014– доцент кафедры акушерства и гинекологии с курсом пренатальной диагностики\r\nокончила СГМА в  1999 г по специальности лечебное дело (диплом с отличием).\r\n2001 – окончила ординатуру по специальности акушерство и гинекология СГМА (курсы по повышению квалификации по данной специальности проходила в 2006, 2010, 2015, 2020 годах). \r\n2001 – учеба по диагностической и хирургической эндоскопии в гинекологии. \r\n2021 – лапароскопия в гинекологии, лапароскопический шов (г Санкт – Петербург)\r\n2002 – специализация по ультразвуковой диагностике СГМА (курсы по повышению квалификации по УЗД проходила в 2007,  2012, 2017, 2021 годах)\r\n2013 – пренатальная диагностика нарушений развития ребенка  (РМАНПО г Москва); \r\n2018 – пренатальная (дородовая) диагностика нарушений развития ребенка 2 триместр (РМАНПО г Москва)\r\n2018 – детальный осмотр сердца плода («Медика» ,г Санкт – Петербург), \r\n2021 – обучение в РМАНПО на курсе тематического усовершенствования «Ультразвуковая диагностика поверхностно расположенных органов)\r\nЗащитила кандидатскую дисертацию, \r\n2003 – присуждена ученая степень кандидата медицинских наук.', 0, NULL, 0, 0, '\r\n'),
